function maapstd = normmapstd(Signal, norm)
%Signaal in the 1-D vector 
%norm is an 1:to normalise with max valuse , 0:Just standardize 

     mu = round(mean(Signal),3);
     SD = round(std(Signal),3);
     %figure; plot(Signal);
     maapstd = (Signal - mu)./SD;
     %figure; plot(maapstd);
     
     %if normalise is active 
     if (norm == 1)
        maxV = abs(max(maapstd));
        maapstd = maapstd/maxV;
     end
     %figure; plot(maapstd);
     
end    


